class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        self.nums = nums
        return self._countOfPairs(len(nums)-1, 50, 0)

    @cache
    def _countOfPairs(self, pointer, prev_num1, prev_num2) -> int:
        if pointer < 0: return 1

        ans = 0
        for num1 in range(min(self.nums[pointer], prev_num1) + 1):
            num2 = self.nums[pointer] - num1
            if num2 < prev_num2: break
            ans += self._countOfPairs(pointer-1, num1, num2)
        return ans%(10**9 + 7)
