class Solution {
    int mod = 1e9 + 7;
    long long check(vector<int>& nums , int ind , int last1 , vector<vector<int>>& dp){
        int n = nums.size();
        if(ind == n) return 1;
        if(dp[ind][last1] != -1) return dp[ind][last1];
        int last2 = 51;
        if(ind > 0) last2 = nums[ind - 1] - last1;

        int cur = nums[ind];
        long long tot = 0;
        for(int i = 0 ; i <= cur ; i++){
            if(i >= last1 && (cur - i) <= last2){
                tot = (tot + check(nums , ind+1 , i , dp)) % mod;
            }
        }
        return dp[ind][last1] = tot;
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(n+1,vector<int>(52 , -1));
        return (int)(check(nums , 0 , 0 , dp) % mod);
    }
};
