class Solution {
    int mod = 1_000_000_000+7;
    int[][][] dp;
    int n;
    int[] arr;
    int rec(int level,int p1,int p2){
        if(level == n){
            return 1;
        }
        if(dp[level][p1][p2] != -1)return dp[level][p1][p2];
        long ans = 0;
        for(int i=0,j=arr[level];i<=arr[level];i++,j--){
            if(i >= p1 && j <= p2){
                ans += rec(level+1,i,j);
                ans %= mod;
            }
        }
        dp[level][p1][p2] = (int)ans;
        return dp[level][p1][p2];
    }
    public int countOfPairs(int[] nums) {
        arr = nums;
        n = nums.length;
        dp = new int[n][51][51];
        for(int i=0;i<n;i++){
            for(int j=0;j<51;j++){
                Arrays.fill(dp[i][j],-1);
            }
        }
        return rec(0,0,50);
    }
}
