class Solution {
public:

    int mod = 1e9+7;
    int dp[2001][55][55];

    int func(int i,int last1,int last2,int n,vector<int>& nums){
        // cout<<i<<" "<<last1<<" "<<last2<<endl;
        if(i==n) return 1;
        if(dp[i][last1][last2]!=-1) return dp[i][last1][last2];


        long long ans = 0;
        for(int j=last1;j<=nums[i];j++){

            int c1 = j;
            int c2 = nums[i]-j;

            if(c2<=last2){
                ans += func(i+1,c1,c2,n,nums);
            }
        }

        // cout<<endl;
        return dp[i][last1][last2] = ans%mod;
    }


    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        memset(dp,-1,sizeof(dp));

        return func(0,0,51,n,nums);
    }
};
