class Solution{
    int n , nums[] , mod = (int) 1e9 + 7;
    Long[][] dp;
    public int countOfPairs(int[] nums){
        n = nums.length;
        this.nums = nums;
        long ret = 0;
        dp = new Long[n][51];
        for(int i = 0 ; i <= nums[0] ; i++){
            ret += finder(1 , i);
            ret %= mod;
        } 
        return (int) ret;
    }
    private long finder(int x , int p){
        if(x == n) return 1;
        if(dp[x][p] != null) return dp[x][p];
        long ret = 0;
        int q = nums[x - 1] - p;
        for(int i = 0 ; i <= nums[x] ; i++){
            int j = nums[x] - i;
            if(i >= p && q >= j) ret += finder(x + 1 , i);
            ret %= mod;
        }
        return dp[x][p] = ret;
    }
}
