class Solution {
public:
    const int MOD = 1e9 + 7;

    int dfs(int i, int l, vector<int>& a, vector<vector<int>>& dp) {
        int n = a.size();
        if (i == n) return 1;  

        if (dp[i][l] != -1) return dp[i][l];  

        int cnt = 0;

        for(int k = l; k <= a[i]; k++) {
            int o = a[i] - k;
            if(i == 0 || (a[i-1] - l >= o)) 
                cnt = (cnt + dfs(i + 1, k, a, dp)) % MOD;
        }

        return dp[i][l] = cnt % MOD;
    }

    int countOfPairs(vector<int>& a) {
        int n = a.size();
        vector<vector<int>> dp(n, vector<int>(1001, -1));  
        return dfs(0, 0, a, dp);  
    }
};
