class Solution {
public:
    #define mod 1000000007
    int dp[2002][51][51];
    int solve(vector<int>&nums,int index,int prev1,int prev2){
        int n=nums.size();
        if(index>=n){
            return 1;
        }
        if(dp[index][prev1][prev2]!=-1){
            return dp[index][prev1][prev2];
        }
        int ans=0;
        for(int i=prev1;i<=nums[index];i++){
            if(nums[index]-i<=prev2){
                ans=(ans+solve(nums,index+1,i,nums[index]-i))%mod;
            }
        }
        return dp[index][prev1][prev2]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        for(int i=0;i<2002;i++){
            for(int j=0;j<51;j++){
                for(int z=0;z<51;z++){
                    dp[i][j][z]=-1;
                }
            }
        }
        return solve(nums,0,0,50);
    }
};
