class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        dp = [[0]*(num+1) for num in nums]
        dp[0] = [1]*(nums[0]+1)
        for i in range(1, len(nums)):
            for arr1 in range(nums[i]+1):
                arr2 = nums[i]-arr1
                for k in range(min(arr1,nums[i-1]-arr2)+1):
                    dp[i][arr1] += dp[i-1][k]
                    dp[i][arr1] %= 1_000_000_007
        ans = 0
        for x in dp[-1]:
            ans += x
            ans %= 1_000_000_007
        return ans
