class Solution {
public:
    long long dp[2001][51][51];
    long long mod=1e9+7;

    long long helper(long long i,long long p1,long long p2,vector<int>& nums){
        if(i==nums.size()) return 1;
        if(dp[i][p1][p2]!=-1) return dp[i][p1][p2];

        long long tot=0;
        for(long long x=p1;x<=50;x++){
            for(long long y=0;y<=p2;y++){
                if(x+y!=nums[i]) continue;
                tot+=helper(i+1,x,y,nums);
                tot=tot%mod;
            }
        }

        return dp[i][p1][p2]=tot%mod;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return helper(0,0,50,nums);
    }
};
