class Solution {
public:
    int mod1=1000000007;
    int rec(int inx,int prev1,vector<int>&nums,vector<vector<int>>&dp){
        if(inx==nums.size()) return 1;
        int ans=0;
        if(dp[inx][prev1]!=-1) return dp[inx][prev1];
        int prev2=(inx-1>=0)?(nums[inx-1]-prev1):102;
        for(int currno=prev1;currno<=nums[inx];currno++){
            int currno2=(nums[inx]-currno);
            if(prev2>=currno2) ans=(ans%mod1 + rec(inx+1,currno,nums,dp)%mod1)%mod1;

        }
        return dp[inx][prev1]=ans%mod1;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<int>>dp(nums.size(),vector<int>(51,-1));
        return rec(0,0,nums,dp);
    }
};
