/**
 * @param {number[]} nums
 * @return {number}
 */
var countOfPairs = function(nums) {
    const mod = 10**9+7;
     dp = new Array(nums.length);
    for (let i = 0; i < nums.length; i+=1) {
        const mat = new Array(51);
        for (let j = 0; j < 51; j += 1) {
            const arr = new Array(51);
            arr.fill(-1);
            mat[j]=(arr);
        }
        dp[i]=(mat);
    }
    const fill = (i,a,b) => {
        if (i >= nums.length) {
            return 1;
        }
        if (dp[i][a][b] === -1) {
            let here = 0;
            for (let c = a; c <=nums[i]; c += 1) {
                if (c >= a && nums[i]-c <=b) {
                    here += fill(i+1, c, nums[i]-c);
                    here = here %mod;
                }
            }
            dp[i][a][b]=here;
        }
        return dp[i][a][b];
    }
    return fill(0, 0, nums[0]);
};
