#define ll long long

class Solution {
public:
    ll dp[2005][55];
    ll mod=1e9+7;

    ll helper(ll idx,ll arr1,vector<int> &nums) {
        if(idx>=(ll)nums.size()) {
            return 1;
        }

        if(dp[idx][arr1]!=-1) {
            return dp[idx][arr1];
        }

        ll count=0;
        if(idx==0) {
            for(int i=0; i<=nums[idx]; i++) {
                count=(count+helper(idx+1,i,nums))%mod;;
            }
        }
        else {
            ll lastx=arr1;
            ll lasty=nums[idx-1]-lastx;

            for(int i=lastx; i<=nums[idx]; i++) {
                ll newy=nums[idx]-i;
                if(newy<=lasty) {
                    count=(count+helper(idx+1,i,nums))%mod;
                }
            }
        }

        return dp[idx][arr1]=count;
    }

    int countOfPairs(vector<int>& nums) {
        ll n=nums.size();
        for(int i=0; i<=n; i++) {
            for(int j=0; j<=52; j++) {
                dp[i][j]=-1;
            }
        }

        ll ans=helper(0,0,nums);

        return ans;
    }   
};
