class Solution {
public:
    const int MOD = 1e9+7;

    int helper(int i, int pincr, vector<int>& nums,vector<vector<int>> &dp){
        if(i==nums.size()) return 1;
        if(dp[i][pincr]!=-1) return dp[i][pincr];
        int ans = 0;
        if(i==0){
            for(int j=0;j<=nums[i];j++){
                if(nums[i]-j>=0)
                    ans += helper(i+1,j,nums,dp);
                    ans = ans % MOD;
            }
        }
        else{
            int pdecr = nums[i-1] - pincr ;
            for(int j=0;j<=50;j++){
                if(j>=pincr and (nums[i]-j <= pdecr) and nums[i]-j>=0)
                    ans += helper(i+1,j,nums,dp);
                    ans = ans % MOD;
            }
        }
        return dp[i][pincr] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<int>> dp(nums.size(),vector<int>(51,-1));
        return helper(0,0,nums,dp);
    }
};
