#define ll long long int
class Solution{
public:
ll solve(int a,int b,int c,const vector<int>& d,vector<vector<vector<ll>>>& e){
if(a>=d.size())return 1;
if(e[a][b][c]!=-1)return e[a][b][c];
ll f=0;
ll g=1e9+7;
for(int h=b;h<=d[a];++h){
for(int i=c;i>=0;--i){
if(h+i==d[a]){
f+=solve(a+1,h,i,d,e);
f%=g;
}
}
}
return e[a][b][c]=f;
}
int countOfPairs(vector<int>& d){
int j=d.size();
vector<vector<vector<ll>>> e(j,vector<vector<ll>>(51,vector<ll>(51,-1)));
return solve(0,0,d[0],d,e);
}
};
