class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        M = int(1e9+7)

        @cache
        def f(k, v):
            if k == n:
                return 1
            ans = 0
            for i in range(v, nums[k]+1):
                j = nums[k] - i
                if k >= 1 and nums[k-1] - v < j:
                    continue
                ans = (ans + f(k+1, i)) % M
            return ans

        return f(0, 0)
