class Solution {
public:
    const int mod=1e9+7;
    int sum[51][51];
    int dp[2001][51],k,ans;
    int n;
    int countOfPairs(vector<int>& nums) {
        n=nums.size();
        for(int i=1; i<=n; i++){
            for(int j=0; j<=nums[i-1]; j++){
                if(i==1) dp[i][j]=1;
                else{
                    k=nums[i-1]-j;
                    for(int z=0; z<=nums[i-2]; z++){
                        int y=nums[i-2]-z;
                        if(y>=k && z<=j){
                            dp[i][j]=(dp[i][j]+dp[i-1][z])%mod;
                        }
                    }
                }
            }

        }
        for(int i=0; i<=50; i++) ans=(ans+dp[n][i])%mod;
        return ans;
    }
};
