const int MOD = 1e9 + 7;

int madd(int x, int y) {
    return x + y >= MOD ? x + y - MOD : x + y;
}

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        unordered_map<int, int> mem;
        function<int(int,int,int)> dfs = [&](int pos, int p1, int p2) {
            if (pos == n) return 1;
            int key = pos << 6 | p1;
            if (mem.count(key)) return mem[key];
            auto& res = mem[key];
            int val = nums[pos];
            for (int i = max(p1, val - p2); i <= val; ++i) {
                res = madd(res, dfs(pos + 1, i, val - i));
            }
            return res;
        };
        return dfs(0, 0, nums[0]);
    }
};
