class Solution {
public:
    const int MOD = 1e9 + 7;

    int solve(int idx, int prev1, int prev2, vector<int> &nums,vector<vector<vector<int> > > &dp) {
        if (idx == nums.size()) return 1; 

        if (dp[idx][prev1][prev2] != -1) 
            return dp[idx][prev1][prev2];  

        int c = 0;
        for (int i = prev1; i <= nums[idx]; i++) { 
            int j = nums[idx] - i;
            if (j <= prev2) {
                c = (c + solve(idx + 1, i, j, nums,dp)) % MOD;
            }
        }

        return dp[idx][prev1][prev2] = c;
    }

    int countOfPairs(vector<int>& nums) {
        vector<vector<vector<int> > > dp(nums.size()+1,vector<vector<int> >(51,vector<int>(51,-1)));
        return solve(0, 0, 50, nums,dp);  
    }
};
