class Solution {
public:
    #define md 1000000007
    int solve(int i,vector<int>&nums,int lt,unordered_map<int,unordered_map<int,int>>&dp){
        if(i==nums.size()){
            return 1;
        }
        if(dp.find(i)!=dp.end() && dp[i].find(lt)!=dp[i].end()) return dp[i][lt];
        int ans=0;
        if(lt==51){
            for(int j=0;j<=nums[i];j++){
                ans=(ans%md+solve(i+1,nums,j,dp)%md)%md;
            }
        }
        else{
            int monod=lt;
            int monoi=nums[i-1]-lt;
            if(nums[i]<monoi) return 0;
            for(int j=0;j<=lt;j++){
                if(nums[i]-j<monoi) break;
                ans=(ans%md+solve(i+1,nums,j,dp)%md)%md;
            }
        }
        return dp[i][lt]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        unordered_map<int,unordered_map<int,int>>dp;
        return solve(0,nums,51,dp);
    }
};
