class Solution {
public:
    const int MOD = 1e9 + 7;

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(101, 0));

        // Initialize dp for the first element
        for (int j = 0; j <= nums[0]; ++j) {
            dp[0][j] = 1;
        }

        for (int i = 1; i < n; ++i) {
            vector<int> new_dp(101, 0);

            for (int j = 0; j <= nums[i]; ++j) {
                int arr2_i = nums[i] - j;
                if (arr2_i < 0 || arr2_i > 100)
                    continue;

                for (int k = 0; k <= j; ++k) {
                    int arr2_i_minus_1 = nums[i - 1] - k;
                    if (arr2_i_minus_1 >= arr2_i) {
                        new_dp[j] = (new_dp[j] + dp[i - 1][k]) % MOD;
                    }
                }
            }

            dp[i] = new_dp;
        }

        // Sum up all valid sequences for the last element
        int result = 0;
        for (int j = 0; j <= 100; ++j) {
            result = (result + dp[n - 1][j]) % MOD;
        }

        return result;
    }
};
