class Solution {
private:
    using ll = long long;
    const ll mod = 1e9 + 7;
    vector<vector<ll>> dp;

    int helper(int ind , int prevNum , vector<int>& nums) {
        int n = nums.size();
        if(ind >= n) return 1;

        if(dp[ind][prevNum + 1] != -1) return dp[ind][prevNum + 1];
        ll ans = 0;
        for(int i = 0; i <= nums[ind]; i++) {
            int x = i;
            int y = nums[ind] - i;

            if(prevNum == -1) {
                ans += helper(ind + 1 , x , nums);
                ans %= mod;

            }
            else {
                int prevx = prevNum;
                int prevy = nums[ind - 1] - prevNum;

                if(x >= prevx and y <= prevy) {
                    ans += helper(ind + 1 , x , nums);
                    ans %= mod;
                }
            }
        }
        return dp[ind][prevNum + 1] = ans;
    }
public:
    int countOfPairs(vector<int>& nums) {
        ll n = nums.size();
        dp.assign(n , vector<ll>(53 , -1));
        return helper(0 , -1 , nums);
    }
};
