class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        M = 10 ** 9 + 7
        @lru_cache(None)
        def count(i, n1):
            if i == len(nums):
                return 1
            n2 = nums[i - 1] - n1
            res = 0
            for t1 in range(n1, nums[i] + 1):
                t2 = nums[i] - t1
                if t2 < 0:
                    break
                if t2 > n2:
                    continue
                res += count(i + 1, t1)
            return res % M

        return sum(count(1, n1) for n1 in range(nums[0] + 1)) % M
