class Solution {
public:
    const int mod = 1e9 + 7;
    int dp[2001][51][51];
    int solve(int i, int prev1,int prev2, vector<int>& nums) {
    if (i >= nums.size()) {
        return 1;
    }
    if(dp[i][prev1][prev2]!=-1) return dp[i][prev1][prev2];
    int result = 0;
    for (int j = prev1; j <= nums[i]; ++j) {
         if((nums[i]-j)<=prev2 && (nums[i]-j)>=0){
             result=(result%mod+solve(i+1,j,nums[i]-j,nums)%mod)%mod;
         }
    }
    return dp[i][prev1][prev2]=result%mod;
}

    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return solve(0,0,nums[0],nums);
    }
};
