#define ll long long
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        ll n=nums.size(), i, j, k, s, res=0, M=1e9+7;

        ll dp[n][51][51];
        for(i=0;i<n;i++){
            for(j=0;j<51*51;j++){
                dp[i][j/51][j%51]=0;
            }
        }
        for(i=0;i<=nums[0];i++){
            dp[0][i][nums[0]-i]=1;
        }
        for(i=1;i<n;i++){
            for(j=0;j<=nums[i];j++){
                for(int l=0;l<=nums[i-1];l++){
                    if(l<=j && nums[i-1]-l>=nums[i]-j){
                        dp[i][j][nums[i]-j] = (dp[i-1][l][nums[i-1]-l]+dp[i][j][nums[i]-j])%M;
                    }
                }
            }
        }
        for(i=0;i<51*51;i++){
            res=(res+dp[n-1][i/51][i%51])%M;
        }
        return res;

    }
};
