class Solution {
public:
    int dp[2001][51];
    int mod=1e9+7;

    int fun(int i, int il, vector<int> &a){
        if(i==a.size()) return 1;

        int dl;
        long long ans=0;

        if(il==-1){
            il=0; dl=50;
        }
        else dl=a[i-1]-il;

        if(dp[i][il]!=-1) return dp[i][il];


        for(int x=a[i]; x>=il; x--){
            int y=a[i]-x;
            if(y>dl) break;
            if(y>=0) ans=(ans+fun(i+1, x, a))%mod;
        }

        return dp[i][il]=ans;
    }

    int countOfPairs(vector<int>& a) {
        memset(dp, -1, sizeof(dp));
        return fun(0, -1, a);
    }
};
