class Solution {
public:
    int mod;
    long long check(vector<int>& nums, vector<vector<int>> &dp,int idx,int prev1,int prev2){
        if(idx>=nums.size()) return 1;
        if(dp[idx][prev1] != -1) return dp[idx][prev1];
        int n = 0;
        long long val = 0;
        while(n <= nums[idx]){
            if( n >= prev1 &&  nums[idx]-n <= prev2){
                val=(val+check(nums,dp,idx+1,n,nums[idx]-n))%mod;
            }
            n++;
        }
        return dp[idx][prev1] = val;
    }
    int countOfPairs(vector<int>& nums) {
        int prev1= 0;
        int prev2= 100;
        mod = 1e9+7;
        int sz  = nums.size();
        vector<vector<int>> dp(sz,vector<int>(51,-1));

        return check(nums,dp,0,prev1,prev2);

    }
};
