class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        ans = 0
        n = len(nums)
        mod = 10 ** 9 + 7

        @cache
        def helper(idx, prevIn, prevDe):
            nonlocal ans

            if idx == n:
                return 1

            res = 0
            for currIn in range(prevIn, nums[idx] + 1):
                currDe = nums[idx] - currIn
                if currDe > prevDe:
                    continue
                else:
                    res = (res + helper(idx + 1, currIn, currDe)) % mod

            return res

        return helper(0, 0, float('inf'))
