class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10**9 + 7
        n = len(nums)

        dp = [[-1]* 51 for _ in range(n)]
        def recur(idx, prev1):
            if idx== n:
                return 1

            if dp[idx][prev1]!= -1:
                return dp[idx][prev1]

            ans = 0
            prev2 = 0

            if idx ==0:
                prev2 = 51
            else:
                prev2 = nums[idx-1]- prev1

            for i in range(nums[idx]+1):
                if i >= prev1 and (nums[idx]- i) <= prev2:
                    ans+= recur(idx+1, i)
                    ans%= mod

            dp[idx][prev1]= ans
            return ans

        return recur(0, 0) % mod
