class Solution {
public:
    int m=1e9+7;
    vector<int>a;
    int n;
    int dp[2001][52][52];
    int rec(int i,int last1,int last2)
    {
        if(i==n)
        {
            return 1;
        }
        if(dp[i][last1][last2]!=-1)
        {
            return dp[i][last1][last2];
        }
        int ans=0;
        for(int x=0;x<=50;x++)
        {
            int f=x;
            int s=(a[i]-f);
            if(f>=0 && s>=0 && f>=last1 && s<=last2)
            {
                ans=(ans+rec(i+1,f,s))%m;
            }
        }
        return dp[i][last1][last2]= ans;
    }
    int countOfPairs(vector<int>& nums) {
        a=nums;
        n=nums.size();
        memset(dp,-1,sizeof(dp));
        return rec(0,0,51);
    }
};
