int dp[2001][51][51];
int mod = 1e9 + 7;
class Solution {
public:
    int n;
    vector<int> v;

    int f(int i, int p1, int p2) {
        if(i >= n) return 1;

        if(dp[i][p1][p2] != -1) return dp[i][p1][p2];

        int ans = 0;
        for(int x = p1; x <= v[i]; x++) {
            int y = v[i] - x;
            if(y >= 0 && y <= p2) {
                ans = (ans + f(i+1, x, y)) % mod;
            }
        }
        return dp[i][p1][p2] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        v = nums; n = v.size();
        for(int i = 0; i < n; i++) {
            for(int j = 0; j <= 50; j++) {
                for(int k = 0; k <= 50; k++) dp[i][j][k] = -1;
            }
        }
        return f(0, 0, 50);
    }
};
