class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        constexpr int MOD = 1e9 + 7;
        const int n = nums.size();
        int m = *max_element(nums.begin(), nums.end());
        if (m == 0) return 1;
        vector<vector<int>> dp(n, vector<int>(m + 1));
        for (int j = 0; j <= m; ++j) {
            dp[0][j] = 1;
        }
        for (int i = 1; i < n; ++i) {
            vector<int> tmp(m + 1);
            tmp[0] = dp[i - 1][0];
            for (int j = 1; j <= nums[i - 1]; ++j) {
                tmp[j] = tmp[j - 1] + dp[i - 1][j];
                tmp[j] %= MOD;
            }

            int x = 0, y = nums[i - 1] - nums[i];
            for (int j = 0; j <= nums[i]; ++j, ++x, ++y) {
                int z = min(x, y);
                if (z < 0 || z > m) continue;
                dp[i][j] = tmp[z];
            }
        }
        int ans = 0;
        for (int j = 0; j <= m; ++j) {
            ans += dp[n - 1][j];
            ans %= MOD;
        }
        return ans;
    }
};
