class Solution {
public:
    int mod = 1e9 + 7;
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return getResult(0, 0, nums[0], nums, dp);
    }
    int getResult(int i, int min1, int max2, vector<int>& nums, vector<vector<vector<int>>>& dp) {
        if (i == nums.size()) return 1;
        if (dp[i][min1][max2] != -1) return dp[i][min1][max2];
        int result = 0;
        for (int j = min1; j <= nums[i]; ++j) {
            int k = nums[i] - j;
            if (k > max2) continue;
            int curResult = getResult(i + 1, j, k, nums, dp);
            result = (result + curResult) % mod;
        }
        return dp[i][min1][max2] = result;
    }
};
