class Solution:
    def countOfPairs(self, ss: List[int]) -> int:
        MOD = 10 ** 9 + 7
        n = len(ss)
        d = [[] for _ in range(n)]
        for x in range(ss[0] + 1):
            d[0].append((x, 1))

        # x - increasing
        # y - decreasing
        for i in range(1, n):
            for x in range(ss[i] + 1):
                y = ss[i] - x
                xval = 0

                for px, pval in d[i-1]:
                    py = ss[i-1] - px
                    if x >= px and y <= py:
                        xval += pval

                d[i].append((x, xval % MOD))

        ans = sum(item[1] for item in d[-1]) % MOD

        return ans
