class Solution {
    int mod = 1e9 + 7;

    int f(int ind, vector<vector<vector<int>>> &dp, int ma, int mi, vector<int>& nums) {
        if (ind == nums.size())
            return 1;
        if (dp[ind][ma][mi] != -1)
            return dp[ind][ma][mi];

        int num = 0;

        for (int i = 0; i <= ma; i++) {
            if (i <= nums[ind] && nums[ind] - i >= mi) {
                num += f(ind + 1, dp, i, nums[ind] - i, nums);
                num = num % mod;
            }
        }

        return dp[ind][ma][mi] = num;
    }

public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));
        int ans = 0;

        for (int i = 0; i <= nums[0]; i++) {
            ans += f(1, dp, i, nums[0] - i, nums);
            ans = ans % mod;
        }

        return ans;
    }
};
