typedef long long int  ll;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        const int M = 1e9+7;
        vector<vector<vector<ll>>>dp(n+1,vector<vector<ll>>(51,vector<ll>(51)));
        for (int i = 0; i < 51; ++i) {
            for (int j = 0; j < 51; ++j) {
                if(nums[0] == i+j)dp[0][i][j]=1;
            }
        }
        for (int i = 1; i < n; ++i) {
            for (int x = 0; x < 51; ++x) {
                for (int y = 0; y < 51; ++y) {
                    int p = nums[i-1]-y;
                    int q = nums[i]-x;
                    if(p<0 || q<0 || p>x || y<q)continue;
                    dp[i][x][q] = (dp[i][x][q]+dp[i-1][p][y])%M;
                }
            }
        }
        ll ans = 0;
        for (int i = 0; i < 51; ++i) {
            for (int j = 0; j < 51; ++j) {
                ans =  (ans +dp[n-1][i][j])%M;
            }
        }
        return ans;
    }
};

