class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7

        @cache
        def dfs(ind, prev):
            # print(ind, prev)
            if ind == len(nums):
                return 1

            ans = 0
            for v1 in range(prev, nums[ind]+1):
                v2 = nums[ind] - v1

                if ind == 0:
                    ans += dfs(ind+1, v1)
                    ans %= MOD
                elif ind > 0 and nums[ind-1]-prev >= v2:
                    ans += dfs(ind+1, v1)
                    ans %= MOD

            return ans

        return dfs(0, 0)
