class Solution {
public:
    const int mod=1e9+7;
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        int mx=*max_element(nums.begin(),nums.end());
        vector<vector<int>> dp(n,vector<int>(mx+1,-1));
        function<int(int,int)> solve=[&](int i,int prev){
            if(i==n) return 1;
            if(dp[i][prev]!=-1) return dp[i][prev];
            int ans=0;
            for(int j=0;j<nums[i]+1;j++){
                if(j>=prev && (nums[i-1]-prev)>=(nums[i]-j)) ans=(ans + solve(i+1,j))%mod;
            }
            return dp[i][prev]=ans;
        };
        int ans=0;
        for(int i=0;i<=nums[0];i++){
            ans=(ans + solve(1,i))%mod;
        }
        return ans;
    }
};
