class Solution {
private:
    const int mod = int(1e9+7);
    int add(int a,int b) {
        return ((a%mod) + (b%mod)) % mod;
    }
    int dp[2001][51][51];
    int f(int i, int a1, int a2, vector<int> &nums) {
        // base cases
        if(i == nums.size()) return 1;
        if(dp[i][a1][a2] != -1) return dp[i][a1][a2];

        int res = 0;
        for(int num=a1; num<=nums[i]; ++num) {
            int num2 = nums[i] - num;
            if(num2 <= a2) {
                res = add(res, f(i+1, num, num2, nums));
            }
        }
        return dp[i][a1][a2] = res;
    }
public:
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return f(0,0,50,nums);
    }
};
