class Solution {
public:
    int MOD = 1e9 + 7;
    int dp[2001][51][52];
    int f(int i, int prevInc, int prevDec, vector<int>& nums) {
        if (i >= nums.size()) {
            return 1;
        }
        if (dp[i][prevInc][prevDec] != -1) {
            return dp[i][prevInc][prevDec];
        }
        long long res = 0;
        for (int j = prevInc; j <= nums[i]; j++) {
            if ((nums[i]-j) <= prevDec) {
                res += f(i+1, j, nums[i]-j, nums);
            }
        }
        return dp[i][prevInc][prevDec] = (res % MOD);
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return f(0, 0, 51, nums);
    }
};
