class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10**9+7
        @cache
        def dp(num1, i):
            num2 = nums[i]-num1
            if i == len(nums)-1:
                return 1

            ret = 0
            for x in range(num1, nums[i+1]+1):
                if nums[i+1]-x <= num2:
                    ret += dp(x, i+1)
            return ret%mod

        ans = 0
        for num1 in range(nums[0]+1):
            ans += dp(num1, 0)
        return ans%mod
