#define MOD 1000000007
class Solution {
    long long int solve(vector<int> &nums, int n, int i, int preva, int prevb, vector<vector<vector<long long int>>> &dp){
        if(i >= n) return 1;
        // cout<<i<<" "<<preva<<" "<<prevb<<endl;

        if(dp[i][preva][prevb] != -1){
            return dp[i][preva][prevb]%MOD;
        }
        long long int res = 0;
        for(int k = preva; k<=nums[i]; k++){
            int num1 = k;
            int num2 = nums[i] - k;
            if(prevb < num2 || num1<0 || num2<0){
                continue;
            }
            res += solve(nums, n, i+1, num1, num2, dp)%MOD;
            res = res%MOD;
        }
        return dp[i][preva][prevb] = res%MOD;
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<long long int>>> dp(n+1, vector<vector<long long int>> (51, vector<long long int> (51, -1)));
        long long int ans = solve(nums, n, 0, 0, 50, dp);

        return ans;
    }
};
