class Solution {
    int dp[2005][55][55];
    int MOD = 1000000007;

    long long gogo(vector<int>& nums, int mini, int maxi, int i = 0) {
        if(i == nums.size())
            return 1;

        if(dp[i][mini][maxi] != -1)
            return dp[i][mini][maxi];

        long long ans = 0;
        for(int j = mini; j <= nums[i]; j++) {
            if(nums[i] - j > maxi) {
                continue;
            }

            ans += gogo(nums, j, nums[i] - j, i + 1);
            ans %= MOD;
        }

        return dp[i][mini][maxi] = ans;
    }
public:
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));

        int maxi = *max_element(nums.begin(), nums.end());

        int ans = gogo(nums, 0, maxi);

        return ans;
    }
};
