int f(int i, int amin, int bmax, auto &v, auto &dp) {
    if (i == v.size())
        return 1;
    if (dp[i][amin][bmax] == -1) {
        int t = 0;
        for (int a = 0; a <= v[i]; ++a) {
            int b = v[i] - a;
            if (amin <= a && b <= bmax)
                t = mod_add(t, f(i + 1, a, b, v, dp));
        }
        dp[i][amin][bmax] = t;
    }
    return dp[i][amin][bmax];
}

class Solution {
