class Solution {
    int MAX = Integer.MAX_VALUE;
    int mod = ((int)Math.pow(10,9))+7;
    int[][][] dp;
    public int countOfPairs(int[] nums) {
        dp= new int[nums.length][51][51];
        for(int i=0;i<nums.length;i++){
            for(int i1=0;i1<51;i1++){
                Arrays.fill(dp[i][i1],-1);
            }
        }
        return rec(0,0,50,nums);
    }
    int rec(int i,int pi,int pj,int[] nums){
        if(i==nums.length){
            return 1;
        }
        if(dp[i][pi][pj]!=-1){
            return dp[i][pi][pj];
        }

        int sum = nums[i];
        int res = 0;
        for(int li=0;li<=sum;li++){
            int rj= sum-li;
            if(pi<=li && rj<=pj){
                res = add(res, rec(i+1,li,rj,nums));
            }
        }
        return dp[i][pi][pj] = res;
    }
    int add(int a,int b){
        return ((a%mod)+(b%mod))%mod;
    }
}
