#include <vector>
using namespace std;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(51, 0));

        for (int j = 0; j <= nums[0]; ++j) {
            dp[0][j] = 1;
        }

        for (int i = 1; i < n; ++i) {
            for (int j = 0; j <= nums[i]; ++j) {
                int sum = 0;
                for (int k = 0; k <= min(j, nums[i-1]); ++k) {
                    if (nums[i] - j <= nums[i-1] - k) {
                        sum = (sum + dp[i-1][k]) % MOD;
                    }
                }
                dp[i][j] = sum;
            }
        }

        int result = 0;
        for (int j = 0; j <= nums[n-1]; ++j) {
            result = (result + dp[n-1][j]) % MOD;
        }

        return result;
    }
};
