class Solution {
public:
    int dp[2001][52];
    const int mod = 1e9+7;
    int rec(int idx,int n,vector<int>&nums,int prev1){
        if(idx==n){
            return 1;
        }
        if(dp[idx][prev1+1]!=-1)return dp[idx][prev1+1];
        int ans = 0;
        int num = nums[idx];
        int prev2 = -1;
        if(idx>0){
            prev2 = nums[idx-1] - prev1;
        }
        if(prev1==-1){
            for(int i=0;i<=num;i++){
                int first = i;
                int sec = num - i;
                ans = (ans + rec(idx+1,n,nums,first))%mod;
            }
        }
        else{
            for(int i=prev1;i<=num;i++){
                int first = i;
                int sec = num-i;
                if(first>=prev1 && sec<=prev2){
                    ans = (ans + rec(idx+1,n,nums,first))%mod;
                }
            }
        }
        return dp[idx][prev1+1] = ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return rec(0,nums.size(),nums,-1);
    }
};
