class Solution {
public:
    int MOD=pow(10,9)+7;
    int helper(vector<int> &nums,int i,int n,int prev,int left,vector<vector<int>>&dp){
        if(i>=n){
            return 1;
        }
        if(dp[i][prev]!=-1){
            return dp[i][prev];
        }
        int ans=0;
        for(int k=prev;k<=nums[i];k++){
            if(left>=(nums[i]-k)){
                ans=(ans+helper(nums,i+1,n,k,nums[i]-k,dp))%MOD;
            }
        }
        return dp[i][prev]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<int>> dp(n,vector<int>(52,-1));
        return helper(nums,0,n,0,52,dp);
    }
};
