class Solution {
public:
    int dp[2001][51][51],n,mod=1e9+7;
    int see(vector<int> &nums,int i,int prev1,int prev2)
    {
        if(i==n)
        {
            return 1;
        }
        else if(dp[i][prev1][prev2]!=-1)
        {
            return dp[i][prev1][prev2];
        }
        else
        {
            int ans=0;
            for(int j=0;j<=50;j++)
            {
                if((j)>=prev1 && (nums[i]-j)<=prev2 && (nums[i]-j)>=0)
                {
                    ans=(ans+see(nums,i+1,j,(nums[i]-j)))%mod;
                }
            }
            return dp[i][prev1][prev2]=ans;
        }
    }
    int countOfPairs(vector<int>& nums) 
    {
        n=nums.size();
        memset(dp,-1,sizeof(dp));
        return see(nums,0,0,50);
    }
};
