class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        m = int(1e9) + 7

        @cache
        def calc(i, prevI):
            ans = 0

            if i == len(nums):
                return 1

            for k in range(prevI, nums[i]+1):
                # currD = nums[i] - k
                # if currD >= prevD:
                prevD = float('inf')
                if i > 0:
                    prevD = nums[i-1] - prevI
                currD = nums[i] - k

                if currD <= prevD:
                    ans += calc(i+1, k)%m


            return ans%m

        return calc(0, 0)
