class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)

        @cache
        def dp(i, lb, ub):
            if i == n:
                return 1

            cur = nums[i]
            res = 0

            for x in range(cur+1):
                y = cur-x
                if x >= lb and y <= ub:
                    res += dp(i+1, x, y)
            return res

        return dp(0, -inf, inf)%(10**9+7)
